import React from 'react';
import "./Mega.css"

export default class Mega extends React.Component {
    state = {
        numero: this.props.numeroInicial || 0,
    };
    gerarNumeros = () => {
       const qtde = props; 
       const min = 1;
       const max = 60;
       let bilhete = [];
        for(var i=0; i<=qtde; i++){
            var numero = Math.ceil(Math.random()*(max-min)+min);
            if(bilhete.includes(numero)){
                numero = Math.ceil(Math.random()*(max-min)+min);
            }else {
                bilhete.push(numero);
            }
        }
        return bilhete;
    }
    render(){
        return(
            <div className='Mega'>
                <h2>Mega Sena - Surpresinha</h2>
                <div>
                    <label htmlFor="bilhete"></label>
                    <input type="number" />
                </div>
                <button>Gerar</button>
            </div>
        )
    }
}
