import React from 'react';
import "./Mega.css"

export default class Mega extends React.Component {
    state = {
        qtde: this.props.qted,
    };

    render(){
        return(
            <div className='Mega'>
                <h2>Mega Sena - Surpresinha</h2>
                <div>
                    <label htmlFor="bilhete"></label>
                    <input type="number" />
                </div>
                <button onClick={this.state.gerarNumeros(qtde)}>Gerar</button>
            </div>
        )
    }
}
