import React from "react";
import produtos from '../../data/produtos';
import './TabelaProdutos.css'

export default props => {
    const line = produtos.map((produto, i) => {
        return (
                <tr className= {i} key={produto.id}>
                    <td>
                        {produto.id}
                    </td>
                    <td>
                        {produto.produto}
                    </td>
                    <td>
                       R$ {produto.preco}
                    </td>
                </tr>
        );
    });                
    return (
        <div className="TabelaProdutos">
            <table border="1">
                <tr>
                    <th>ID</th>
                    <th>Produto</th>
                    <th>Pre√ßo</th>
                </tr>
                    {line}
            </table>
        </div>
    )
}
