import React from 'react';

export default props => {
    function gerarNumeros(props){
       const qtde = props; 
       const min = 1;
       const max = 60;
       let bilhete = [];
        for(var i=0; i<=qtde; i++){
            var numero = Math.ceil(Math.random()*(max-min)+min);
            if(bilhete.includes(numero)){
                numero = Math.ceil(Math.random()*(max-min)+min);
            }else {
                bilhete.push(numero);
            }
        }
        return bilhete;
    }
    return(
        <div>
            <h2>Mega Sena - Surpresinha</h2>
            <div>
                <label htmlFor="bilhete"></label>
                <input type="number" />
            </div>
            <button>Gerar</button>
        </div>
    )
}
